import React, { useEffect, useRef, useState } from 'react'
import Chat from './Chat'
import './App.css'
import axios from 'axios'

function App() {
  const [messages, updateMessages] = useState([])
  const messageRef = useRef()

  useEffect(() => {
    axios.get('http://127.0.0.1:5000').then(function (response){
      updateMessages(m=>{return response.data.messages})
    })
  }, []);

  function handleSend(e) {
    if (e.key !== "Enter") return
    const text = messageRef.current.value
    if (text === '') return
    axios.post('http://127.0.0.1:5000',{'username': 'yasir', 'message': text}).catch(e=>console.log(e)).then(
      updateMessages(m=>{return [...m, {'username': 'yasir', 'message': text}]})
    )
    messageRef.current.value = null

  }
  return (
    <><main>
        <Chat className='chat' messages = {messages}/>
      </main>
      <div className='bot'>
        <input onKeyDown={handleSend} ref={messageRef} type='text' placeholder='Enter a message...'/>
      </div>
    </>


  )
}

export default App;
