import React, { useEffect, useRef, useState } from 'react'
import Chat from './Chat'
import './App.css'


function App() {
  const [messages, send] = useState([])
  const messageRef = useRef()

  useEffect(()=>{document.addEventListener('keydown', detectkey, true)},[])

  const detectkey = (e) => {
    console.log(e.key)
  }

  function handleSend(e) {
    const text = messageRef.current.value
    if (text === '') return
    send(m=>{return [...m, text]})
    messageRef.current.value = null

  }
  return (
    <><main>
        <Chat className='chat' messages = {messages}/>
      </main>
      <input ref={messageRef} type='text' placeholder='Enter a message...'/>
      <button onClick={handleSend}>  Send</button>
    </>


  )
}

export default App;
