import React, { useEffect, useRef, useState } from 'react'
import Chat from './Chat'
import './App.css'
import { BrowserRouter as Router, Route, Routes, useNavigate} from 'react-router-dom'
import { io } from 'socket.io-client'
const socket = io("http://127.0.0.1:5000");

function App() {
  const [messages, updateMessages] = useState([])
  const [loggedIn, setLoggedIn] = useState(false)
  const [username, setUsername] = useState()
  const messageRef = useRef()
  const nameRef = useRef()
  const navigate = useNavigate()



  useEffect(()=>{
    socket.on("connection success", (data)=>{
      updateMessages(m=>{return data.logs})
    })
  })

  useEffect(()=>{
    socket.on("receive message", (data)=>{
      console.log(data.message)
      updateMessages(m=>{return [...m, data.message]})
    })
  }, [])

  function handleSend(e) {
    if (e.key !== "Enter") return
    const text = messageRef.current.value
    if (text === '') return
    console.log("here")
    socket.emit('send message', {'username': username, 'message': text})
    updateMessages(m=>{return [...m, {'username': username, 'message': text}]})
    messageRef.current.value = null

  }

  function handleLogin(e){
    if (e.key !== "Enter" && e._reactName !== 'onClick') return
    const text = nameRef.current.value
    if (text === '') return
    setUsername(text)
    setLoggedIn(true)
    console.log(loggedIn)
    navigate('/chat')
    nameRef.current.value = null
  }
  return (
    <Router>
      <Routes>


        <Route path='/chat' element={
          <div className='total'>
            <div className='chatbox'>
              <Chat className='chat' messages = {messages}/>
            </div>
            <div className='bot'>
              <input className='chatInput' onKeyDown={handleSend} ref={messageRef} type='text' placeholder='Enter a message...'/>
            </div>
          </div>
        }/>


        <Route path='/' element={
          <div>
            <div className='login'>
              <div className='prompt'>Pick a username</div>
              <input className='loginInput' onKeyDown={handleLogin} ref={nameRef} type='text' placeholder='Username'/>
              <button className='joinChat' onClick={handleLogin}>Join chat</button>
            </div>
          </div>
        }/>


      </Routes>
    </Router>

  )
}

export default App;
