import React, { useEffect, useRef, useState } from 'react'
import Chat from './Chat'
import './App.css'
// import axios from 'axios' 
// import { io } from 'socket.io-client'
// const socket = io.connect('http://127.0.0.1:5000')

function App() {
  const [messages, updateMessages] = useState([])
  const messageRef = useRef()
  const [loggedIn, setLoggedIn] = useState(false)



  useEffect(()=>{
    // socket.on("connection success", (data)=>{
    //   console.log(data.logs)
    //   updateMessages(m=>{return data.logs})
    // })
  })

  function handleSend(e) {
    if (e.key !== "Enter") return
    const text = messageRef.current.value
    if (text === '') return
    // socket.emit('send message', {'username': 'yasir', 'message': text})
    updateMessages(m=>{return [...m, {'username': 'Yasir', 'message': text}]})
    messageRef.current.value = null

  }
  return (
    <>
      {loggedIn? (
        <div className='total'>

        <div className='chatbox'>
          <Chat className='chat' messages = {messages}/>
        </div>

        <div className='bot'>
          <input onKeyDown={handleSend} ref={messageRef} type='text' placeholder='Enter a message...'/>
        </div>


      </div>

      ):(
        <div>

        </div>
      )}

    </>

  )
}

export default App;
