import React, { useContext, useState} from 'react'
// import { UserContext } from './UserContext'
// import {useNavigate} from 'react-router-dom'
import { io } from 'socket.io-client'
import { UserContext } from './UserContext';
const socket = io("http://127.0.0.1:5000");

const Signup = () => {
    const [signName, setSignName] = useState()
    const [signPassword, setSignPassword] = useState()
    const {x} = useContext(UserContext)

    const handleSignup = ()=>{
        if(!signName  || !signPassword) return
        if(x) console.log('')
        console.log(signName, signPassword)
        socket.emit('sign up attempt', {'username': signName, 'password': signPassword})
        socket.on('sign up success', ()=>console.log("success"))
        socket.on('sign up failure', ()=>console.log("failure"))
    }

    return ( 
        <div className='card'>
            <div className='prompt'> Sign up</div>
            <input className='signupInput' onChange={(e)=>setSignName(e.target.value)} placeholder='Choose a username'/>
            <input className='signupInput' onChange={(e)=>setSignPassword(e.target.value)} placeholder='Choose a password'/>
            <button onClick={handleSignup}> Sign up!</button>
        </div>

     );
}
 
export default Signup;
