import React, { useRef, useContext} from 'react'
import { UserContext } from './UserContext'
import {useNavigate} from 'react-router-dom'



const Login = () => {
    const nameRef = useRef()
    const navigate = useNavigate()
    const {setUsername} = useContext(UserContext)

    function handleLogin(e){
        if (e.key !== "Enter" && e._reactName !== 'onClick') return
        const text = nameRef.current.value
        if (text === '') return
        setUsername(text)
        navigate('/chat')
        nameRef.current.value = null
      }

    return (  
        <div>
            <div className='card'>
                <div className='prompt'>Pick a username</div>
                <input className='loginInput' onKeyDown={handleLogin} ref={nameRef} type='text' placeholder='Username'/>
                <button onClick={handleLogin}>Join chat</button>
            </div>
        </div>
    );
}
 
export default Login;
